(342, 1, 'hrbeu', 39, 'Bachelor in Chemical Engineering and Technology', '2009-05-02 06:46:29', '2009-05-02 06:46:29', 'hrbeu', 'Bachelor in Chemical Engineering and Technology', 'Bachelor in Chemical Engineering and Technology', '<div class="Section1" style="layout-grid:  15.6pt none">\r\n<p align="justify"><span style="font-size: x-small"><b><span lang="EN-US">Package 1:</span></b><span lang="EN-US"> </span></span></p>\r\n<p align="justify"><span style="font-size: x-small"><b><span lang="EN-US">Introduction:</span></b><span lang="EN-US"> We are committed to train and cultivate senior engineering professionals who systematically master the modern theory, technical process and testing technology of modern electrochemistry and electrochemistry engineering, and are able to work in scientific researching, teaching, engineering, technical developing and managing in some enterprises and public institutions related to electrochemistry . </span><span lang="EN-US">This Program is also open for international students.</span></span></p>\r\n<p align="justify"><span style="font-size: x-small"><b><span lang="EN-US">Core classes:</span></b><span lang="EN-US">&nbsp; inorganic chemistry, organic chemistry, analytic chemistry, physical chemistry, theoretical electrochemistry, electrochemical measuring, surface chemistry, application electronic technique, circuit basic science, power-supply processing and electrochemical reaction process and so on.</span></span></p>\r\n<p align="justify"><span style="font-size: x-small"><b><span lang="EN-US">Package 2:</span></b><span lang="EN-US"> </span></span></p>\r\n<p align="justify"><span style="font-size: x-small"><span lang="en-us"><b>Introduction</b></span><b><span lang="EN-US">: </span></b><span lang="EN-US">We are committed to train and cultivate senior engineering professionals who master chemistry engineering and processing, study the basic law and the best processing methods in the chemical process, and can do chemistry engineering designing, technique developing, manufacturing technique, managing and scientific researching in chemistry industry, oil refining industry, metallurgical industry, energy industry, light industry, medicine industry, material industry and defense industry. </span><span lang="EN-US">This Program is also open for international students.</span></span></p>\r\n<p align="justify"><span style="font-size: x-small"><span lang="EN-US"><b>Core classes:</b>&nbsp; inorganic chemistry, organic chemistry, analytic chemistry, physical chemistry, chemical industry theory, chemical reaction engineering, chemical industry technical process designing, chemical industry machinery equipment, chemical industry instrument &amp; its automation and chemical industry systematic engineering and so on.</span></span></p>\r\n</div>', NULL, 108, 1, ''),
(343, 1, 'hrbeu', 39, 'Bachelor in Environmental Engineering', '2009-05-02 06:47:02', '2009-05-02 06:47:02', 'hrbeu', 'Bachelor in Environmental Engineering', 'Bachelor in Environmental Engineering', '<div class="Section1" style="layout-grid:  15.6pt none">\r\n<p align="justify"><span style="font-family: Arial"><span style="font-size: x-small"><b><span lang="EN-GB">Introduction: </span></b><span lang="EN-US">We are committed to train and cultivate senior technicians who master the basic knowledge and application ability in chemistry, environmental engineering and environmental science in order to be able to work on protection technique researching, designing, applying and developing of such pollutions as waste water, waste gas and waste solid materials.</span></span></span></p>\r\n<p align="justify"><span style="font-family: Arial"><span style="font-size: x-small"><b><span lang="EN-GB">Major benefits for students: </span></b><span lang="EN-US">After the theoretical study and the practice training, the graduates will master basic knowledge of chemistry, environmental science and environmental engineering, grasp basic methods and principles of water pollution treatment, atmospheric pollution control and the treatment and exercitation of waste solid materials, have the ability of environment contamination control and treatment, environment monitoring, environment influence evaluation, environment management and environment programming, comprehend the forward position and trend of environment science and environment engineering techniques and are able to undertake environment scientific researching and practical work.</span></span></span></p>\r\n<p align="justify"><span style="font-family: Arial"><span style="font-size: x-small"><b><span lang="EN-US">Graduates'' career prospects:</span></b><span lang="EN-US"> The graduates can choose government departments, programming departments, economy management departments, environment protection departments, designing units, industrial and mining enterprises, scientific research units and universities to work on programming, designing, constructing, managing, teaching, researching and developing.</span></span></span></p>\r\n<p align="justify"><span style="font-family: Arial"><span style="font-size: x-small"><b><span lang="EN-US">Core classes:</span></b><span lang="EN-US"> chemistry, environmental microbiology, environmental ecology, environmental monitoring, environment''s influence evaluation, water pollution control engineering and atmospheric pollution control engineering.</span></span></span></p>\r\n</div>', NULL, 105, 1, ''),
(344, 1, 'hrbeu', 39, 'Bachelor in Material Chemistry', '2009-05-02 06:47:40', '2009-05-02 06:47:40', 'hrbeu', 'Bachelor in Material Chemistry', 'Bachelor in Material Chemistry', '<div class="Section1" style="layout-grid:  15.6pt none">\r\n<p align="justify"><span style="font-size: x-small"><span lang="EN-US"><b>Introduction:</b> We are committed to cultivate senior professionals who master the basic knowledge in chemistry, material science, solid physics and quantum chemistry. This Program contains the training directions in material composition, material design, material engineering application and material property evaluation. Its character is based on the learning of material chemistry, macromolecule material, material physics, inorganic functional material, metal material science, nanometer material science and biological material. </span><span lang="EN-US">This Program is also open for international students.</span></span></p>\r\n<p align="justify"><span style="font-size: x-small"><b>Graduates''</b><span lang="EN-US"> <b>career prospects:</b> The graduates may start their career in national science research institute, universities and colleges to do the scientific researching and teaching. Meanwhile, they can also go to enterprises and companies in chemistry industry for scientific researching, manufacturing developing and managing.</span></span></p>\r\n<p align="justify"><span style="font-size: x-small"><span lang="EN-US"><b>Core classes:</b> inorganic chemistry, organic chemistry, analytic chemistry ,physical chemistry, theoretical electro-chemistry, electrochemistry measuring, surface chemistry, material physics, macromolecule material, solid chemistry, composite material, inorganic material chemistry, structural chemistry, biological chemistry, catalytic chemistry, material corrosion &amp; protection, fine ceramic process, thin-film material technique, surface &amp; interface chemistry and modern functional material.</span></span></p>\r\n</div>', NULL, 115, 1, ''),
(345, 1, 'hrbeu', 39, 'Ph.D in Materialogy', '2009-05-02 06:49:09', '2009-05-02 06:49:09', 'hrbeu', 'Ph.D in Materialogy', 'Ph.D in Materialogy', '<div class="Section1" style="layout-grid:  15.6pt none">\r\n<p align="justify"><span style="font-size: x-small"><b><span lang="en-us">Research fields:</span></b></span></p>\r\n<blockquote>\r\n<ol>\r\n    <li>\r\n    <p class="MsoNormal" align="justify"><span style="font-size: x-small"><span lang="EN-US">Structural materials and strength</span> </span></p>\r\n    </li>\r\n    <li>\r\n    <p class="MsoNormal" align="justify"><span style="font-size: x-small"><span lang="EN-US">Synthesis and<span style="color: #ff6600"> </span>Preparation of functional materials</span> </span></p>\r\n    </li>\r\n    <li>\r\n    <p class="MsoNormal" align="justify"><span style="font-size: x-small"><span lang="EN-US">Corrosion and protection</span> </span></p>\r\n    </li>\r\n    <li>\r\n    <p class="MsoNormal" align="justify"><span style="font-size: x-small"><span lang="EN-US">Properties and applications of specific optical materials</span> </span></p>\r\n    </li>\r\n    <li>\r\n    <p class="MsoNormal" align="justify"><span style="font-size: x-small"><span lang="EN-US">Microstructure and properties of nano-materials</span> </span></p>\r\n    </li>\r\n    <li>\r\n    <p class="MsoNormal" align="justify"><span style="font-size: x-small"><span lang="EN-US">Failure Analysis of Structural Components </span></span></p>\r\n    </li>\r\n    <li>\r\n    <p class="MsoNormal" align="justify"><span style="font-size: x-small"><span lang="EN-US">Brittle Fracture control and mechanism of alloy steels</span> </span></p>\r\n    </li>\r\n    <li>\r\n    <p align="justify"><span style="font-size: x-small"><span lang="EN-US">Thermo-spraying and composite surface Engineering</span> </span></p>\r\n    </li>\r\n</ol>\r\n</blockquote>\r\n<p align="justify"><span style="font-size: x-small"><b><span lang="en-us">Core classes: </span></b><span lang="EN-US">Chinese, Chinese General Situation, Advanced Applied Math, </span><span lang="EN-US">Materials Design, Theory of Material Strength, Functional Materials, Second</span><span lang="EN-US">&nbsp; </span><span lang="EN-US">Foreign Language, Academic Report Seminar, Optical Material and Polarized Light, Theory of Corrosion and Protection</span></span></p>\r\n</div>', NULL, 110, 1, '');
