(2, '【#MY_acm2010_top】', '<style type="text/css">\r\n<!--\r\nul{ list-style:none; margin:0px; padding:0px;} \r\n\r\n#top_logo{ width:960px; height:123px; text-align:left;}\r\n#top_nav{ width:960px; height:36px; border-top:#bfbdbd 1px solid; border-bottom:#bfbdbd 1px solid; line-height:36px;}\r\n.top_nav_li{ float:left; padding:0px 22px 0px 22px; font-family:Tahoma; font-size:14px; font-weight:bold; color:#000000;}\r\n.top_nav_li a{ color:#000000; text-decoration:none;}\r\n.top_nav_li a:hover{ text-decoration:underline;}\r\n-->\r\n</style>\r\n\r\n<div id="top_logo"><a href="showcolumn.php?columnid=48"><img src="images/ACM_logo.jpg"></a></div>\r\n<div id="top_nav">\r\n	<ul>\r\n    	<li class="top_nav_li" style="padding-right:90px"><a href="showcolumn.php?columnid=48">Home</a></li>\r\n        <li class="top_nav_li"><a href="showcolumn.php?columnid=55">News</a></li>\r\n        <li class="top_nav_li"><a href="showcolumn.php?columnid=49">Event schedule</a></li>\r\n        <li class="top_nav_li"><a href="showcolumn.php?columnid=50">Harbin China</a></li>\r\n        <li class="top_nav_li"><a href="showcolumn.php?columnid=51">HEU</a></li>\r\n        <li class="top_nav_li"><a href="showcolumn.php?columnid=52">Hotels</a></li>\r\n        <li class="top_nav_li"><a href="showcolumn.php?columnid=53">Travel Information</a></li>\r\n        <li class="top_nav_li"><a href="showcolumn.php?columnid=54">Contact</a></li>\r\n    </ul>\r\n</div>\r\n'),
(3, '【#MY_acm2010_left】', '<style type="text/css">\r\n<!--\r\n#index_main_left_title{ font-size:13px; color:#000000; font-weight:bold; line-height:20px; text-align:left; padding:0px 0px 0px 12px}\r\n#index_main_left_content{  color:#000000; line-height:18px; text-align:justify; padding:0px 12px 0px 12px;}\r\n#index_main_left_sponsors{  text-align:right; padding-right:18px;}\r\n-->\r\n</style>\r\n\r\n<div id="index_main_left_title">Finals in Harbin<br>1~6 February, 2010</div>\r\n<div style="height:10px;"></div>\r\n<div id="index_main_left_content">Welcome to the 34th ACM-ICPC World Finals, sponsored by IBM. Harbin Engineering University is the proud host of this year’s contest. Five days during the "Harbin International Ice and Snow Sculpture Festival" promises to bring new and exciting experiences to the World Finals participants.  HEU welcomes the world to Harbin!</div>\r\n<div style="height:20px;"></div>\r\n<div id="index_main_left_sponsors"><a href="http://www.ibm.com/developerworks/university/students/contests/acm/index.html" target="_blank"><img src="images/ACM_Sponsors_IBM.png" /></a></div>'),
(4, '【#MY_acm2010_right】', '<SCRIPT language=javascript>\r\ndocument.write();function show_time(){\r\nwindow.setTimeout("show_time()", 1000);\r\nBirthDay=new Date("02-01-2010"); //改成你的计时日期 月-日-年\r\ntoday=new Date();\r\ntimeold=(BirthDay.getTime()-today.getTime());\r\nsectimeold=timeold/1000\r\nsecondsold=Math.floor(sectimeold);\r\nmsPerDay=24*60*60*1000\r\ne_daysold=timeold/msPerDay\r\ndaysold=Math.floor(e_daysold);\r\ne_hrsold=(e_daysold-daysold)*24;\r\nhrsold=Math.floor(e_hrsold);\r\ne_minsold=(e_hrsold-hrsold)*60;\r\nminsold=Math.floor((e_hrsold-hrsold)*60);\r\nseconds=Math.floor((e_minsold-minsold)*60);\r\nshow_countdown.innerHTML=daysold+"D "+hrsold+"H "+minsold+"M "+seconds+"S" ;\r\n}\r\nshow_time();\r\n</SCRIPT>\r\n\r\n<style type="text/css">\r\n<!--\r\n#index_main_right_countdown_title{ color:#000000; line-height:15px; text-align:right;}\r\n#index_main_right_countdown{ height:39px; background:#7d7d7d; font-size:21px; color:#FFFFFF; text-align:center; line-height:39px;}\r\n.index_main_right_title{ height:25px; background:url(images/ACM_right_titlebg.png) repeat-x; line-height:25px; font-family:Verdana; font-size:12px; font-weight:bold; color:#000000; padding-left:10px;}\r\n.index_main_right_Links_content{ background:#f5f5f5 url(images/ACM_right_icon.gif) no-repeat top left; color:#6a6a6a; padding:11px 0px 0px 22px; line-height:18px;}\r\n.index_main_right_Links_content a{ color:#6a6a6a; text-decoration:none;}\r\n.index_main_right_Links_content a:hover{ color:#000000; text-decoration:underline;}\r\n.index_main_right_contact_content{ background:#f5f5f5; color:#6a6a6a; padding:8px 0px 12px 18px; line-height:24px;}\r\n.index_main_right_contact_content a{ text-decoration:none;}\r\n.index_main_right_contact_content a:hover{ text-decoration:none; color:#000000;}\r\n-->\r\n</style>\r\n\r\n<div id="index_main_right_countdown_title">Countdown to<br />2010 ACM/ICPC World Final</div>\r\n<div id="index_main_right_countdown"><span id="show_countdown"></span></div>\r\n<div style="height:5px;"></div>\r\n<div class="index_main_right_title">Links</div>\r\n<div class="index_main_right_Links_content"><a href="http://cm.baylor.edu/" target="_blank">ACM / ICPC official website</a></div>\r\n<div class="index_main_right_Links_content"><a href="http://necpc.acm-icpc.cn/" target="_blank">ACM / ICPC Northeast China<br />Organizing Committee</a></div>\r\n<div class="index_main_right_Links_content"><a href="http://acm-icpc.cn/cms/icpc/live/index_cn.html" target="_blank">ACM / ICPC China Steering<br />Committee</a></div>\r\n<div class="index_main_right_Links_content"><a href="http://english.hrbeu.edu.cn/" target="_blank">Harbin Engineering University</a></div>\r\n<div style="height:14px; background:#f5f5f5;"></div>\r\n<div class="index_main_right_title">Contact</div>\r\n<div class="index_main_right_contact_content">Phone: 86-451-82518662<br />E-mail: <a href="mailto:icpc2010@hrbeu.edu.cn">icpc2010@hrbeu.edu.cn</a><br>\r\n<a href="mailto:acm_icpc_2010@yahoo.com">acm_icpc_2010@yahoo.com</a></div>');
